def factorialize(num):
  factorial = 1
  for i in range(num, 0, -1):
    factorial *= i

  return factorial

def sqrt(n):
    low = 0
    high = n

    while low <= high:
        mid = (low + high) // 2
        if mid * mid == n:
            return mid
        elif mid * mid < n:
            low = mid + 1
        else:
            high = mid - 1

    return low - 1

