def sort_ascending(lst):
    for i in range(len(lst)):
        for j in range(i+1, len(lst)):
            if lst[i] > lst[j]:
                lst[i], lst[j] = lst[j], lst[i]
    return lst

sort_ascending([7, 3, 5, 10, 1])  # returns [1, 3, 5, 7, 10]

def sqrt(n):
    low = 0
    high = n

    while low <= high:
        mid = (low + high) // 2
        if mid * mid == n:
            return mid
        elif mid * mid < n:
            low = mid + 1
        else:
            high = mid - 1

    return low - 1

