import 'dart:async';
import 'dart:convert';
import 'package:http/http.dart' as http;

void main() async {
  try {
    var response = await http.get(Uri.parse('http://httpstat.us/200?sleep=5000'),).timeout(Duration(seconds: 3)); // Make an HTTP GET request to a test endpoint that has a delay of 5 seconds
    if (response.statusCode == 200) {
      var data = jsonDecode(response.body);
      print('Response: ${data['status']}'); // Print the status of the response
    } else {
      print('Error: ${response.statusCode} ${response.reasonPhrase}'); // Print an error message if the response is unsuccessful
    }
  } on TimeoutException catch (e) {
    print('Error: $e'); // Print a timeout error message if the HTTP request times out
  } catch (e) {
    print('Error: $e'); // Print any other unexpected errors that occur
  }
}
```

