app.use(bodyParser.json({ strict: false })); 

const serverlessAuthentication = require('./serverless-authentication'); 

serverlessAuthentication.server.serializeUser((user, done) => { 
    done(null, user.id); 
}); 

serverlessAuthentication.server.deserializeUser((id, done) => { 
