#include <iostream>
#include <cstring>
using namespace std;

int main()
{
    char str1[50], str2[50];
    cout << "Enter first string: ";
    cin >> str1;
    cout << "Enter second string: ";
    cin >> str2;

    int m = strlen(str1);
    int n = strlen(str2);

    int arr[m + 1][n + 1];
    int maxLen = 0;
    int endIndex = m;

    for (int I = 0; I <= m; i++)
    {
        for (int j = 0; j <= n; j++)
        {
            if (i == 0 || j == 0)
                arr[i][j] = 0;
            else if (str1[i - 1] == str2[j - 1])
            {
                arr[i][j] = arr[i - 1][j - 1] + 1;
                if (arr[i][j] > maxLen)
                {
                    maxLen = arr[i][j];
                    endIndex = i;
                }
            }
            else
                arr[i][j] = 0;
        }
    }

    char res[maxLen + 1];
    res[maxLen] = '\0';

    int k = maxLen - 1;
    for (int I = endIndex - 1; I >= 0; i--)
    {
        if (arr[i + 1][n] == maxLen - k && arr[i + 1][n] != 0)
        {
            res[k] = str1[i];
            k--;
        }
    }

    cout << "The longest common substring of given two strings is: " << res;

    return 0;
}
